using System.Collections;using System.Collections.Generic;using UnityEngine;public class LeftTrigger : MonoBehaviour{    private Animator anim = null;        void Start()    {        // animatorコンポーネントを取得        anim = GetComponent<Animator>();    }    void Update()    {        float horizontalKey = Input.GetAxis("Horizontal");                if (horizontalKey < 0)        {            anim.SetBool("Left", true);        }        else        {            anim.SetBool("Left", false);        }    }}